select.execute:

var resp, header, h,
    data = <resources/>,
    headers = <headers/>,
    req = y.rest(url);

// set user agent
if (ua) {
    req.header('User-Agent', ua);
}

// fetch url
resp = req.get();

// get headers
for (header in resp.headers) {
    //h = <header/>;
    h = <{header}>{resp.headers[header]}</{header}>;
    //h.@["key"] = header;
    //h.@["value"] = resp.headers[header];
    headers.headers += h;
}

// set result
data.resources += <url>{url}</url>;
data.resources += <status>{resp.status}</status>;
data.resources += headers;
data.resources += <content>{resp.response}</content>;
response.object = data;
