select.execute:
            var results = "";
            var keys = [];

            for (k in text) {
               keys.push(k);
            }

            keys.sort();

            for (var i=0;i<keys.length;i++) {
               key = keys[i];
               value = text[key];
               results += value;
            }

			results = <entry><text>{results}</text></entry>;

            response.object = results;
