select.execute:

var yahoo = new Namespace("http://where.yahooapis.com/v1/schema.rng");
// namespace is needed because the <place> element in the results of the geo.places
// table is in this namespace, and otherwise it will not be addressed by simply
// saying res.place

var lat,lon;

if ( /^[0-9.]+,[0-9.]+$/.test(place) ) {
    lat = place.match(/^[0-9.]+/);
    lon = place.match(/[0-9.]+$/);
} else {
    var res = y.query("select * from google.geocoding where q='" + 
                      place + "'").results;
    lat = res..Point.coordinates[1];
    lon = res..Point.coordinates[2];
}

response.object = <item><geohash>{encodeGeoHash(lat,lon)}</geohash></item>;

// copied from https://github.com/davetroy/geohash-js/blob/master/geohash.js
function encodeGeoHash(latitude, longitude) {
    var BITS = [16, 8, 4, 2, 1];
    var BASE32 = "0123456789bcdefghjkmnpqrstuvwxyz";
    var is_even=1;
	var i=0;
	var lat = []; var lon = [];
	var bit=0;
	var ch=0;
	//var precision = 12; // parametrized
	geohash = "";

	lat[0] = -90.0;  lat[1] = 90.0;
	lon[0] = -180.0; lon[1] = 180.0;

	while (geohash.length < precision) {
	  if (is_even) {
			mid = (lon[0] + lon[1]) / 2;
	    if (longitude > mid) {
				ch |= BITS[bit];
				lon[0] = mid;
	    } else
				lon[1] = mid;
	  } else {
			mid = (lat[0] + lat[1]) / 2;
	    if (latitude > mid) {
				ch |= BITS[bit];
				lat[0] = mid;
	    } else
				lat[1] = mid;
	  }

		is_even = !is_even;
	  if (bit < 4)
			bit++;
	  else {
			geohash += BASE32[ch];
			bit = 0;
			ch = 0;
	  }
	}
	return geohash;
}
