} else {
	if (typeof(s1)=='undefined')
		request.query("s1","0");
	if (typeof(s2)=='undefined')
		request.query("s2","10");
	if (typeof(v1)=='undefined')
		request.query("v1","0");
	if (typeof(v2)=='undefined')
		request.query("v2","10");
	if (typeof(p1)=='undefined')
		request.query("p1","0");
