		var url = request.url + "&m=1&m=2&m=3&m=4";
	} else {
		url = request.url;
		for each (r in MPAA) {
			url += "&m=" + ["NR","G","PG","PG-13","R","NC-17"].indexOf(r);
		}
	}
}
