select.execute:

//ref: http://openid.net/specs/openid-authentication-2_0.html#normalization_example

function normalizeIdentifier (identifier) {

    var pattern = '^(http[s]*)?'//scheme
            + '(:\/\/)?'//separator
            +'([^:?#/]+)'//domain
            +'([^:?#]+)?',//path
        match = identifier.match(new RegExp(pattern)),
        normalized = '';

    if (undefined === match[1]) {
        normalized += 'http';
    } else {
        normalized += match[1];
    }

    normalized += '://';

    if (undefined === match[3]) {
        return false;
    } 

    normalized += match[3];

    // if there's a path
    if (match[4]) {
        normalized += match[4];
    } else {
        normalized += '/';
    }

    return normalized;
}

var normalized = normalizeIdentifier(id);

if (!normalized) {
    response.object = <id state="error" details="domain required, eg example.com"/>
} else {
    response.object = <id state="success">{normalized}</id>
}
